const openMenu=(e,t)=>{const c=document.getElementById(e),n=document.getElementById(t);t&&c.addEventListener("click",(()=>{n.classList.add("show")}))},closeMenu=()=>{const e=document.getElementById("nav-close"),t=document.getElementById("nav-menu"),c=[...document.querySelectorAll(".nav__link")];e&&c&&(e.addEventListener("click",(()=>{t.classList.remove("show")})),c.forEach((e=>{e.addEventListener("click",(()=>{t.classList.remove("show")}))})))};window.onscroll=()=>{const e=document.getElementById("header");this.scrollY>=30?e.classList.add("scroll-header"):e.classList.remove("scroll-header")};const toggleCart=()=>{const e=document.querySelector(".cart"),t=document.querySelectorAll(".nav__shop"),c=document.querySelectorAll(".cart__close");t&&t.forEach((t=>{t.addEventListener("click",(()=>{e.classList.add("show-cart")}))})),c&&c.forEach((t=>{t.addEventListener("click",(()=>{e.classList.remove("show-cart")}))}))};openMenu("nav-toggle","nav-menu"),closeMenu(),toggleCart();